-- 2. Add Hindi translation for the Cruyff Turn article
UPDATE public.blog_posts
SET translations = jsonb_set(
  jsonb_set(
    jsonb_set(
      jsonb_set(
        '{}'::jsonb, 
        '{title,hi}', 
        to_jsonb('क्रूफ टर्न - सरल, लेकिन प्रभावी'::text), 
        true
      ),
      '{content,hi}', 
      to_jsonb('<h3><strong>क्रूफ टर्न का इतिहास</strong></h3><p>जोहान क्रूफ ने पहली बार 1974 के विश्व कप में नीदरलैंड और स्वीडन के बीच ग्रुप स्टेज मैच के दौरान इस चाल को अंजाम दिया। डिफेंडर जान ओल्सन द्वारा बारीकी से मार्क किए जाने और गोल से दूर होने के बावजूद, क्रूफ ने पास देने का नाटक किया लेकिन इसके बजाय गेंद को अपने खड़े पैर के पीछे खींच लिया, 180 डिग्री घूम गए, और जगह में तेजी से आगे बढ़ गए, अपने मार्कर को पीछे छोड़ दिया। हालांकि अन्य खिलाड़ियों ने पहले भी इसी तरह के फेंट्स किए होंगे, लेकिन इस विशेष निष्पादन ने इस चाल को दुनिया भर में ध्यान आकर्षित किया और क्रूफ और "टोटल फुटबॉल" के साथ इसके जुड़ाव को मजबूत किया। यह चाल जल्दी ही "देखने वाले हर किसी के दिमाग में छा गई" और अब यह विश्व स्तर पर फुटबॉल प्रशिक्षण का एक सामान्य हिस्सा है।</p><h3><strong>क्रूफ टर्न कैसे करें</strong></h3><ul><li>गेंद के पास ऐसे जाएं जैसे कि आप पास देने या शूट करने वाले हैं, डिफेंडर का ध्यान आकर्षित करें।</li><li>अंतिम क्षण में, गेंद को आगे मारने के बजाय अपने "पासिंग" पैर के अंदरूनी हिस्से का उपयोग करके इसे अपने खड़े पैर के पीछे खींचें।</li><li>अपने कंधों और कूल्हों को घुमाते हुए अपने शरीर को 180 डिग्री मोड़ें ताकि आप विपरीत दिशा का सामना कर सकें।</li><li>डिफेंडर से दूर तेजी से आगे बढ़ें, गेंद को अपने साथ ले जाएं और हमले या पास के लिए जगह बनाएं।</li></ul><h3><strong>महत्वपूर्ण सुझाव:</strong></h3><ul><li>मुड़ते समय डिफेंडर से गेंद को बचाने के लिए अपने शरीर का उपयोग करें।</li><li>यह चाल तब सबसे अच्छा काम करती है जब आप बारीकी से मार्क किए गए हों, विशेष रूप से टचलाइन के पास या भीड़भाड़ वाले आक्रमण क्षेत्रों में।</li><li>टर्न के बाद प्रभावी ढंग से तेजी लाने के लिए गेंद पर करीबी नियंत्रण और संतुलन बनाए रखें।</li></ul><h3><strong>महत्व और सामरिक उपयोग</strong></h3><p>क्रूफ टर्न उन हमलावरों के लिए मूल्यवान है जिन्हें मार्किंग से बचने, दिशा बदलने या तंग जगहों में अवसर बनाने की आवश्यकता होती है। यह विशेष रूप से विंगर्स और फॉरवर्ड्स के लिए उपयोगी है जो अंदर कटने या बॉक्स के पास डिफेंडर से बचने की तलाश में हैं। यह चाल धोखे, संतुलन और त्वरित दिशा परिवर्तन को मिश्रित करती है, जिससे यह रक्षात्मक रेखाओं को तोड़ने के लिए एक विश्वसनीय उपकरण बन जाता है। यह सभी उम्र के खिलाड़ियों को सिखाई जाने वाली तकनीक बनी हुई है, जो आधुनिक फुटबॉल प्रशिक्षण और रणनीति में क्रूफ की विरासत को मजबूत करती है। क्रूफ टर्न का उपयोग खिलाड़ियों को आक्रमणकारी खेल में एक महत्वपूर्ण बढ़त दे सकता है, जिससे उन्हें डिफेंडरों को चकमा देने और महत्वपूर्ण क्षणों में जगह बनाने में मदद मिलती है।</p><p>क्रूफ टर्न करते समय सामान्य गलतियों में खराब गेंद नियंत्रण, ढाल के लिए शरीर का उपयोग न करना, और गेंद को धक्का देने के बजाय किक करना शामिल है, जिसके परिणामस्वरूप डिफेंडरों के पास कब्जा खो सकता है।</p><h3><strong>विशिष्ट त्रुटियां</strong></h3><ul><li>खिलाड़ी अक्सर गेंद को धीरे से धक्का देने के बजाय किक मारते हैं, जिससे चाल अप्रत्याशित हो जाती है और गेंद को नियंत्रित करना कठिन हो जाता है।</li><li>गेंद और डिफेंडर के बीच प्लांट फुट को रखने में विफल रहने का मतलब है कि चाल गेंद को प्रभावी ढंग से ढाल नहीं देती है, जिससे डिफेंडर के लिए इसे रोकना आसान हो जाता है।</li><li>फर्जी पास या शॉट (कूल्हों, कंधों और बाहों के साथ) को "बेचना" नहीं, चाल को कम आश्वस्त करता है, इसलिए डिफेंडर प्रतिबद्ध नहीं हो सकते हैं या धोखा नहीं खा सकते हैं।</li><li>बहुत धीरे-धीरे मुड़ना या टर्न के बाद तेजी लाना भूल जाना डिफेंडर को ठीक होने का समय देता है और लाभ को कम करता है।</li></ul><h3><strong>तकनीकी मुद्दे</strong></h3><ul><li>टर्न शुरू करते समय खराब टाइमिंग का मतलब हो सकता है कि दबाव से बचने के लिए चाल बहुत जल्दी या बहुत देर से की गई है।</li><li>संतुलन खोना या घुटनों को मोड़ने और नीचे रहने में विफल होना स्थिरता को प्रभावित करता है, जिसके परिणामस्वरूप अक्सर चाल के बाद लड़खड़ाते हुए रिकवरी होती है।</li><li>टर्न से पहले जगह और कोण की पहचान न करना, जिससे बिना किसी लाभ या अधिक डिफेंडरों वाले क्षेत्रों में दिशा परिवर्तन होता है।</li></ul>'::text), 
      true
    ),
    '{excerpt,hi}', 
    to_jsonb('क्रूफ टर्न फुटबॉल में एक प्रतिष्ठित ड्रिब्लिंग चाल है, जिसका नाम डच दिग्गज जोहान क्रूफ के नाम पर रखा गया है, जिसका उपयोग अभी भी खिलाड़ियों द्वारा डिफेंडरों से बचने और मैदान पर दिशा बदलने के लिए व्यापक रूप से किया जाता है। यह तकनीकी प्रतिभा और फुटबॉल बुद्धिमत्ता दोनों का प्रतीक बना हुआ है, जो 1974 फीफा विश्व कप में अपनी ऐतिहासिक शुरुआत के बाद लोकप्रिय हुआ।'::text), 
    true
  ),
  '{category,hi}', 
  to_jsonb('फुटबॉल'::text), 
  true
)
WHERE slug = 'the-cruyff-turn-simple-but-effective';

COMMIT;
